import React from "react";
import "./Chat.css";

function Chat() {
const sendMessage = (e) => {
    e.preventDefault(); //prevents from reloading tg
}



  return (
    <div className="chat">
      <div className="chat__header">
        <h4>To: <span className="chat__channelName">Channel Name</span></h4>
        <strong>Details</strong>
      </div>

      {/* chat body */}
      <div className="chat__input">
          <form>
              <input placeholder="iMessage" type="text"/>
              <button onClick={sendMessage}>Send Message</button>
          </form>
      </div>
    </div>
  );
}

export default Chat;
