  }, [])

  const handleChange = (e) => {
    dispatch(setInput(e.target.value));
  };
  return (
    <div className="search">
      <SearchIcon />
      <input placeholder="search" type="text" onChange={handleChange} />
    </div>
  );
}

export default Search;
